me = 0.0; alphaScheme = 1.0;        redLineScheme = 0.0; greenLineScheme = 0.0; blueLineScheme = 0.0; alphaLineScheme = 1.0;        strokeWidth = 1.0;        textx = 0.0; texty = 0.0; textFontSize = 12.0; textFontType = "Times";    }    }